	function _getPunishedAmount(uint96 amount, uint256 until) internal view returns (uint96) {
		uint256 date = timestampToLockDate(block.timestamp);
		uint96 weight = computeWeightByDate(until, date); /// @dev (10 - 1) * WEIGHT_FACTOR
