interface IDex {
    function insertMarketOrder(
        address _baseToken,
        address _secondaryToken,
        uint256 _amount,
        uint256 _multiplyFactor,
        uint64 _lifespan,
        bool _isBuy
    ) external;

    function tickIsRunning(address _baseToken, address _secondaryToken)
        external
        view
        returns (bool);

    function cancelBuyOrder(
        address _baseToken,
        address _secondaryToken,
        uint256 _orderId,
        uint256 _previousOrderIdHint
    ) external;

    function cancelSellOrder(
        address _baseToken,
        address _secondaryToken,
        uint256 _orderId,
        uint256 _previousOrderIdHint
    ) external;

    function maxOrderLifespan() external returns (uint64);
}


contract ReverseAuction is Initializable, Governed {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;

    // RSK base coin  address(bytes20("RBTC"));
    address constant RBTC = 0x5242544300000000000000000000000000000000;

    event TriggeredMarketOrder(
        IDex dex,
        address baseToken,
        address secondaryToken,
        uint256 amount,
        uint256 multiplyFactor,
        uint64 lifespan,
        bool isBuy
    );
    event TriggeredExit(address tokenOut, uint256 amount, address outputAccount);

    IDex private _dexContract;
    IERC20 private _tokenIn;
    IERC20 private _tokenOut;
    uint256 private _multiplyFactor;
    uint64 private _orderLifespan;
    uint256 private _orderThreshold;
    uint256 private _exitThreshold;
    address private _outputAccount;
    bool private _useBuyOrder;

    WRBTC private _wrbtc;
    bool private _stopped;

    /** @dev Initialize this contract.
        @param _governor The governor address for this contract.
        @param dexContract The address of the DEX contract
        @param tokenIn The address of the in-bound token contract
        @param tokenOut The address of out-bound token contract
        @param outputAccount  The multiply factor
        @param orderLifespan The interval in DEX ticks in which an order is valid. Use 0 to set default (max-ticks) from DEX.
        @param multiplyFactor The multiply factor allows determining the competitiveness of market orders and is used to calculate the price of the token as Market Price * Multiply Factor
        @param orderThreshold The amount threshold of input tokens to trigger a DEX market order
        @param exitThreshold The amount threshold of exchanged output tokens from DEX to trigger a withdraw
        @param useBuyOrder True to use BUY order types, false to switch to SELL type.
    */
    function initialize(
        IGovernor _governor,
        IDex dexContract,
        address tokenIn,
        address tokenOut,
        address outputAccount,
        uint64 orderLifespan,
        uint256 multiplyFactor,
        uint256 orderThreshold,
        uint256 exitThreshold,
        bool useBuyOrder,
        address payable wrbtc
    ) public initializer() {
        require(tokenIn != address(0) && tokenOut != address(0), "Invalid token address");
        require(tokenIn != tokenOut, "Same tokens to exchange");
        require(
            outputAccount != address(0) && outputAccount != address(this),
            "Invalid output account"
        );
        require(orderThreshold != 0 && exitThreshold != 0, "Threshold is zero");
        require(multiplyFactor != 0, "Multiply factor is zero");

        _dexContract = dexContract;
        _tokenIn = IERC20(tokenIn);
        _tokenOut = IERC20(tokenOut);
        _orderThreshold = orderThreshold;
        _exitThreshold = exitThreshold;
        _multiplyFactor = multiplyFactor;
        _orderLifespan = orderLifespan;
        _outputAccount = outputAccount;
        _useBuyOrder = useBuyOrder;
        _wrbtc = WRBTC(wrbtc);

        if (_orderLifespan == 0) {
            _orderLifespan = _dexContract.maxOrderLifespan();
        }

        Governed._initialize(_governor);
    }

    /** @dev Return the current in-bound token balance */
    function getInboundTokenBalance() public view returns (uint256) {
        if (address(_tokenIn) == RBTC) {
            uint256 balance = address(this).balance;
            balance = balance.add(_wrbtc.balanceOf(address(this)));
            return balance;
        }
        return _tokenIn.balanceOf(address(this));
    }

    /** @dev Return the current out-bound token balance */
    function getOutboundTokenBalance() public view returns (uint256) {
        if (address(_tokenOut) == RBTC) {
            uint256 balance = address(this).balance;
            balance = balance.add(_wrbtc.balanceOf(address(this)));
            return balance;
        }
        return _tokenOut.balanceOf(address(this));
    }

    /** @dev Returns `true` if there is enough inbound tokens to trigger exchange orders **/
    function readyToTriggerOrders() public view returns (bool) {
        if (_stopped) {
            // @dev Don't create new orders if contract is stopped
            return false;
        }
        uint256 tokenInBalance = getInboundTokenBalance();
        return (tokenInBalance >= _orderThreshold);
    }

    /** @dev Returns `true` if there is enough outbound tokens to trigger exit **/
    function readyToTriggerExit() public view returns (bool) {
        uint256 tokenOutBalance = getOutboundTokenBalance();
        return (tokenOutBalance >= _exitThreshold);
    }

    /** @dev Triggers an exchange order **/
    function triggerOrders() public {
        if (readyToTriggerOrders()) {
            uint256 amount = getInboundTokenBalance();

            IERC20 tokenInput = _tokenIn;
            if (address(_tokenIn) == RBTC) {
                uint256 unwrapped = address(this).balance;
                if (unwrapped > 0) {
                    _wrbtc.deposit{value: unwrapped}();
                }
                tokenInput = IERC20(address(_wrbtc));
            }

            require(
                tokenInput.approve(address(_dexContract), amount),
                "Must approve in-token txfer to dex"
            );

            IERC20 tokenOutput = _tokenOut;
            if (address(_tokenOut) == RBTC) {
                tokenOutput = IERC20(address(_wrbtc));
            }

            IERC20 baseToken = _useBuyOrder ? tokenInput : tokenOutput;
            IERC20 secondaryToken = _useBuyOrder ? tokenOutput : tokenInput;
            _dexContract.insertMarketOrder(
                address(baseToken),
                address(secondaryToken),
                amount,
                _multiplyFactor,
                _orderLifespan,
                _useBuyOrder
            );
            emit TriggeredMarketOrder(
                _dexContract,
                address(baseToken),
                address(secondaryToken),
                amount,
                _multiplyFactor,
                _orderLifespan,
                _useBuyOrder
            );
        }
    }

    /** @dev Triggers an exit order transferring the exit threshold amount to the output account **/
    function triggerExit() public {
        if (readyToTriggerExit()) {
            uint256 amount = _withdrawOutput(_outputAccount);
            emit TriggeredExit(address(_tokenOut), amount, _outputAccount);
        }
    }

    // --- Getters ---

    function getInboundToken() external view returns (address) {
        return address(_tokenIn);
    }

    function getOutboundToken() external view returns (address) {
        return address(_tokenOut);
    }

    function getMultiplyFactor() external view returns (uint256) {
        return _multiplyFactor;
    }

    function getOrderLifespan() external view returns (uint64) {
        return _orderLifespan;
    }

    function getOrderThreshold() external view returns (uint256) {
        return _orderThreshold;
    }

    function getExitThreshold() external view returns (uint256) {
        return _exitThreshold;
    }

    function getOutputAccount() external view returns (address) {
        return _outputAccount;
    }

    function getUseBuyOrder() external view returns (bool) {
        return _useBuyOrder;
    }

    // --- Setters ---

    function setInboundToken(address tokenIn) public onlyAuthorizedChanger() {
        _tokenIn = IERC20(tokenIn);
    }

    function setOutboundToken(address tokenOut) public onlyAuthorizedChanger() {
        _tokenOut = IERC20(tokenOut);
    }

    function setMultiplyFactor(uint256 multiplyFactor) public onlyAuthorizedChanger() {
        _multiplyFactor = multiplyFactor;
    }

    function setOrderLifespan(uint64 orderLifespan) public onlyAuthorizedChanger() {
        _orderLifespan = orderLifespan == 0 ? _dexContract.maxOrderLifespan() : orderLifespan;
    }

    function setOrderThreshold(uint256 orderThreshold) public onlyAuthorizedChanger() {
        _orderThreshold = orderThreshold;
    }

    function setExitThreshold(uint256 exitThreshold) public onlyAuthorizedChanger() {
        _exitThreshold = exitThreshold;
    }

    function setOutputAccount(address outputAccount) public onlyAuthorizedChanger() {
        _outputAccount = outputAccount;
    }

    function setUseBuyOrder(bool useBuyOrder) public onlyAuthorizedChanger() {
        _useBuyOrder = useBuyOrder;
    }

    function isStopped() public view returns (bool) {
        return _stopped;
    }

    function setStopped(bool stopped) public onlyAuthorizedChanger() {
        _stopped = stopped;
    }

    function canCancelOrders() public view returns (bool) {
        address tokenIn = address(_tokenIn) == RBTC ? address(_wrbtc) : address(_tokenIn);
        address tokenOut = address(_tokenOut) == RBTC ? address(_wrbtc) : address(_tokenOut);

        return !_dexContract.tickIsRunning(address(tokenIn), address(tokenOut));
    }

    function cancelOrders(uint256[] memory orderIds) public onlyAuthorizedChanger() {
        require(_stopped, "revAuction not stopped");

        address tokenIn = address(_tokenIn) == RBTC ? address(_wrbtc) : address(_tokenIn);
        address tokenOut = address(_tokenOut) == RBTC ? address(_wrbtc) : address(_tokenOut);

        require(
            !_dexContract.tickIsRunning(address(tokenIn), address(tokenOut)),
            "Dex tick should not be running"
        );

        function(address, address, uint256, uint256) external cancelOrder;
        cancelOrder = _useBuyOrder ? _dexContract.cancelBuyOrder : _dexContract.cancelSellOrder;

        for (uint256 i = 0; i < orderIds.length; i += 1) {
            uint256 hint = 0;
            uint256 orderId = orderIds[i];
            cancelOrder(address(tokenIn), address(tokenOut), orderId, hint);
        }
    }

    function withdraw(address inputAccount, address outputAccount) public onlyAuthorizedChanger() {
        require(_stopped, "revAuction not stopped");

        _withdrawInput(inputAccount);
        _withdrawOutput(outputAccount);
    }

    function _withdrawInput(address inputAccount) internal {
        if (address(_tokenIn) == RBTC) {
            uint256 unwrapped = address(this).balance;
            if (unwrapped > 0) {
                payable(inputAccount).transfer(unwrapped);
            }
            uint256 wrapped = _wrbtc.balanceOf(address(this));
            if (wrapped > 0) {
                bool success = _wrbtc.transfer(inputAccount, wrapped);
                require(success, "Failed to transfer the funds, aborting.");
            }
        } else {
            uint256 amount = getInboundTokenBalance();
            _tokenIn.safeTransfer(inputAccount, amount);
        }
    }

    function _withdrawOutput(address outputAccount) internal returns (uint256) {
        uint256 amount = getOutboundTokenBalance();
        if (address(_tokenOut) == RBTC) {
            uint256 wrapped = _wrbtc.balanceOf(address(this));
            if (wrapped > 0) {
                _wrbtc.withdraw(wrapped);
            }
            amount = address(this).balance;
            payable(outputAccount).transfer(amount);
        } else {
            _tokenOut.safeTransfer(outputAccount, amount);
        }
        return amount;
    }

    function getWrapper() external view returns (address) {
        return address(_wrbtc);
    }

    function getDex() external view returns (address) {
        return address(_dexContract);
    }

    receive() external payable {
        require(
            address(_tokenIn) == RBTC || address(_tokenOut) == RBTC,
            "RBTC input or output required"
        );
    }
}
